import React from 'react';
import {View, Text,Picker,Block} from 'react-native';
import {Header,Input} from 'react-native-elements';
import {Ionicons } from '@expo/vector-icons';

class NewTask extends React.Component {
    state={
      table:[
        {name:"Toan"},
        {name:"Tieng Viet"},
        {name:"Tieng Anh"}
      ],
      selectedLesson:'',
    }

  render () {
    return (
    <View style={{ flex: 1, backgroundColor:'#fff'}}>
      <Header
          statusBarProps={{ barStyle: 'light-content' }}
          barStyle="light-content"
          centerComponent={{ text: 'New Tasks', style: { color: '#fff', fontSize:25 } }}
          containerStyle={{
            backgroundColor: '#3D6DCC',
            justifyContent: 'center',
          }}
      />
      <Input
        label="Name"
        placeholder='Set name for Task'
        leftIcon={
          <Ionicons name='md-checkbox-outline' size={30} style={{ paddingRight:20 }} />
        }
      />
      <Text>{'\n'}Lesson </Text>
      <View style={{flexDirection: 'row',}}>
        <Ionicons name='ios-list-box' size={30} style={{padding: 10, marginLeft: 15}} />
        <Picker
          selectedValue={this.state.selectedLesson} 
          style={{height: 50, width: 200 }}
          onValueChange={(itemValue, itemIndex) =>
            this.setState({selectedLesson:itemValue})
          }>
          {this.state.table.map((item,i)=>(  
            <Picker.Item label={item.name} value={item.name} key={i}/>
          ))}
        </Picker>
      </View>
      <Input
        label="Note"
        placeholder='Description'
        leftIcon={
          <Ionicons name='ios-create' size={30} style={{ paddingRight:20 }} />
        }
      />

    </View>
  );
  }
}

export default NewTask;
